// Combined: Merge Analysis + Prepare for AI Agent
// This single node replaces both "Code in JavaScript" and "Extract WhatsApp Data"

console.log('=== Processing WhatsApp Message ===');

// Get ALL inputs (from media analysis nodes)
const allInputs = $input.all();
console.log('Total inputs received:', allInputs.length);

// Helper function to extract analysis text from various formats
function extractAnalysisText(data) {
  if (Array.isArray(data)) data = data[0];
  if (!data) return null;

  // OpenAI format: { content: [{ text: "..." }] }
  if (data.content && Array.isArray(data.content)) {
    const textParts = data.content.filter(item => item.text && item.text.length > 10);
    if (textParts.length > 0) return textParts.map(item => item.text).join('\n\n');
  }

  // OpenAI annotations: { annotations: [{ text: "..." }] }
  if (data.annotations && Array.isArray(data.annotations)) {
    const textAnnotations = data.annotations.filter(a => a.text && a.text.length > 10);
    if (textAnnotations.length > 0) return textAnnotations.map(a => a.text).join('\n\n');
  }

  // Gemini format: { content: { parts: [{ text: "..." }] } }
  if (data.content?.parts?.[0]?.text) return data.content.parts[0].text;

  // Gemini candidates: { candidates: [{ content: { parts: [...] } }] }
  if (data.candidates?.[0]?.content?.parts?.[0]?.text) {
    return data.candidates[0].content.parts[0].text;
  }

  // Whisper/Transcription: { text: "..." }
  if (data.text && typeof data.text === 'string' && data.text.length > 10) return data.text;

  // Message field
  if (data.message && typeof data.message === 'string' && data.message.length > 10) return data.message;

  // Direct string
  if (typeof data === 'string' && data.length > 10) return data;

  return null;
}

// Step 1: Find analysis result from inputs
let analysisText = null;
for (const item of allInputs) {
  const extracted = extractAnalysisText(item.json);
  if (extracted) {
    analysisText = extracted;
    console.log('Analysis found, length:', analysisText.length);
    break;
  }
}

// Step 2: Get original webhook data
let webhookData = null;
try {
  webhookData = $('Webhook').first().json;
} catch (e) {
  // Fallback: find webhook in inputs
  for (const item of allInputs) {
    const json = Array.isArray(item.json) ? item.json[0] : item.json;
    if (json?.body?.from_number) {
      webhookData = json;
      break;
    }
  }
}

if (!webhookData?.body) {
  throw new Error('No webhook data found');
}

const body = webhookData.body;
const mediaType = body.media?.type || 'text';
const chatbot = body.chatbot || {};
const api = body.api || {};

// Step 3: Determine final message
let message = body.message || '';
let wasAnalyzed = false;

if (analysisText) {
  message = analysisText;
  wasAnalyzed = true;
} else if (mediaType !== 'text') {
  message = body.message || '[Media received]';
}

// Step 4: Enhance message with media context
let chatInput = message;
if (wasAnalyzed && mediaType !== 'text') {
  const labels = { image: 'Image', audio: 'Audio', video: 'Video', document: 'Document' };
  chatInput = `[${labels[mediaType] || 'Media'} Analysis]\n${message}`;

  if (body.message && body.message !== '[Media file]') {
    chatInput += `\n\n[User's caption: ${body.message}]`;
  }
}

// Step 5: Create contact for session management
const fromNumber = body.from_number || 'unknown';
const contact = {
  id: fromNumber.replace(/[^0-9]/g, ''),
  phone: fromNumber,
  name: fromNumber
};

console.log('Message:', chatInput.substring(0, 100));
console.log('From:', fromNumber);
console.log('Chatbot:', chatbot.name, '| ID:', chatbot.id);
console.log('Media:', mediaType, '| Analyzed:', wasAnalyzed);

// Return formatted data for AI Agent
return {
  json: {
    // AI Agent input
    chatInput: chatInput,

    // Contact for memory session
    contact: contact,
    phone: fromNumber,

    // Chatbot config
    chatbotConfig: {
      id: chatbot.id,
      name: chatbot.name || 'Assistant',
      company: chatbot.company_name || '',
      industry: chatbot.industry || '',
      systemPrompt: chatbot.system_prompt || 'You are a helpful AI assistant.',
      businessContext: chatbot.business_context || '',
      complianceRules: chatbot.compliance_rules || [],
      responseGuidelines: chatbot.response_guidelines || [],
      promptVersion: chatbot.prompt_version || null,
      priceVisible: chatbot.price_visible !== false // Default to true if not set
    },

    // API config for tools
    apiConfig: {
      baseUrl: api.base_url || 'https://xatrtqdgghanwdujyhkq.supabase.co/functions/v1',
      chatbotId: chatbot.id
    },

    // Metadata
    timestamp: new Date().toISOString(),
    messageType: mediaType,
    wasMediaAnalyzed: wasAnalyzed,
    sessionId: `whatsapp_${contact.id}_${Date.now()}`
  }
};

// Format AI Agent output for WhatsApp webhook response
const inputData = $input.first().json;

console.log('=== Formatting AI Response ===');
console.log('Input keys:', Object.keys(inputData));

// Extract AI response - try multiple possible fields
let aiResponse = '';

if (inputData.output) {
  aiResponse = inputData.output;
} else if (inputData.text) {
  aiResponse = inputData.text;
} else if (inputData.response) {
  aiResponse = inputData.response;
} else if (inputData.content) {
  aiResponse = inputData.content;
} else if (typeof inputData === 'string') {
  aiResponse = inputData;
} else {
  // Fallback: stringify the whole object
  aiResponse = 'I\'m sorry, I\'m having trouble generating a response right now. Please try again.';
  console.log('Warning: Could not extract AI response from:', inputData);
}

// Clean up response
aiResponse = String(aiResponse).trim();

console.log('Raw AI Response:', aiResponse.substring(0, 200) + '...');

// Try to parse as JSON if it looks like JSON
let reply = aiResponse;
let images = [];
let documents = [];

if (aiResponse.startsWith('{') || aiResponse.startsWith('[')) {
  try {
    const parsed = JSON.parse(aiResponse);
    console.log('Successfully parsed AI response as JSON');

    // Extract reply, images, and documents from parsed JSON
    reply = parsed.reply || parsed.response || parsed.message || parsed.text || aiResponse;
    images = parsed.images || [];
    documents = parsed.documents || [];

    console.log('Extracted reply:', reply.substring(0, 100));
    console.log('Extracted images:', images.length, 'image(s)');
    console.log('Extracted documents:', documents.length, 'document(s)');
  } catch (e) {
    console.log('AI response is not valid JSON, using as-is');
    // Not JSON, use the raw response
  }
}

// Return in the format expected by WhatsApp service
return {
  json: {
    reply: reply,
    images: images,
    documents: documents
  }
};
