// Extract and prepare data from WhatsApp webhook
const inputData = $input.first().json;

console.log('=== WhatsApp Webhook Received ===');
console.log('Message:', inputData.message);
console.log('From:', inputData.from_number);
console.log('Chatbot:', inputData.chatbot?.name);

// Extract main data
const message = inputData.message || 'Hello';
const fromNumber = inputData.from_number || 'unknown';
const chatbot = inputData.chatbot || {};
const products = inputData.products || [];
const knowledgeBase = inputData.knowledge_base || [];
const conversationHistory = inputData.conversation_history || [];

// Format conversation history for AI context
const formattedHistory = conversationHistory.map(msg => {
  const role = msg.direction === 'inbound' ? 'User' : 'Assistant';
  return `${role}: ${msg.content}`;
}).join('\n');

// Format products for context
const productContext = products.length > 0 
  ? `\n\nðŸ“¦ Available Products:\n${products.map(p => 
      `- ${p.name} (RM ${p.price}) - ${p.description}${p.stock ? ` [Stock: ${p.stock}]` : ''}`
    ).join('\n')}`
  : '';

// Format knowledge base
const kbContext = knowledgeBase.length > 0
  ? `\n\nðŸ“š Knowledge Base:\n${knowledgeBase.map(k => 
      `### ${k.title}\n${k.content}`
    ).join('\n\n')}`
  : '';

// Build comprehensive system prompt
const baseSystemPrompt = chatbot.system_prompt || `You are ${chatbot.name || 'a helpful assistant'}, an AI assistant for ${chatbot.company_name || 'our company'}.`;
const businessContext = chatbot.business_context || '';
const complianceRules = chatbot.compliance_rules?.length > 0
  ? `\n\nâš ï¸ Compliance Rules:\n${chatbot.compliance_rules.map(r => `- ${r}`).join('\n')}`
  : '';
const responseGuidelines = chatbot.response_guidelines?.length > 0
  ? `\n\nâœ… Response Guidelines:\n${chatbot.response_guidelines.map(g => `- ${g}`).join('\n')}`
  : '';

const fullSystemPrompt = `${baseSystemPrompt}

ðŸ¢ Business Context:
${businessContext}${productContext}${kbContext}${complianceRules}${responseGuidelines}

ðŸ’¬ Conversation Context:
You are chatting with: ${fromNumber}
Conversation History:
${formattedHistory || 'This is the start of the conversation.'}

IMPORTANT:
- Be helpful, friendly, and professional
- Use the products and knowledge base information to answer questions
- Follow all compliance rules strictly
- Apply the response guidelines in your communication
- Keep responses concise and relevant to WhatsApp chat format`;

// Build user message with current context
const userMessage = message;

console.log('=== Context Prepared ===');
console.log('Products count:', products.length);
console.log('Knowledge base count:', knowledgeBase.length);
console.log('History messages:', conversationHistory.length);

return {
  json: {
    // Core message data
    message: userMessage,
    from_number: fromNumber,

    // AI context
    system_prompt: fullSystemPrompt,
    user_message: userMessage,

    // Original data (for reference)
    chatbot: chatbot,
    products: products,
    knowledge_base: knowledgeBase,
    conversation_history: conversationHistory,

    // Metadata
    timestamp: new Date().toISOString(),
    chatbot_id: chatbot.id,
    chatbot_name: chatbot.name,

    // Debug info
    debug: {
      hasProducts: products.length > 0,
      hasKnowledgeBase: knowledgeBase.length > 0,
      hasHistory: conversationHistory.length > 0,
      messageLength: userMessage.length
    }
  }
};

// Generate AI response using OpenAI
const inputData = $input.first().json;
const userMessage = inputData.user_message || inputData.message;
const systemPrompt = inputData.system_prompt;

console.log('=== Calling OpenAI ===');
console.log('User message:', userMessage);

// Note: In n8n, you would use the OpenAI Chat Model node
// This code node is a placeholder for the flow
// The actual AI call happens in the OpenAI Chat Model node

return {
  json: {
    user_message: userMessage,
    system_prompt: systemPrompt,
    ready_for_ai: true
  }
};
