// Extract WhatsApp webhook data and prepare for AI Agent
const inputData = $input.first().json;

console.log('=== WhatsApp Webhook Data ===');
console.log('Keys received:', Object.keys(inputData));

// Extract main fields
const message = inputData.message || inputData.body?.message || 'Hello';
const fromNumber = inputData.from_number || inputData.body?.from_number || 'unknown';
const chatbot = inputData.chatbot || inputData.body?.chatbot || {};
const products = inputData.products || inputData.body?.products || [];
const knowledgeBase = inputData.knowledge_base || inputData.body?.knowledge_base || [];
const conversationHistory = inputData.conversation_history || inputData.body?.conversation_history || [];

console.log('Message:', message);
console.log('From:', fromNumber);
console.log('Chatbot:', chatbot.name);
console.log('Products:', products.length);
console.log('KB:', knowledgeBase.length);
console.log('History:', conversationHistory.length);

// Format conversation history
const formattedHistory = conversationHistory.map(msg => {
  const role = msg.direction === 'inbound' ? 'User' : 'Assistant';
  const time = new Date(msg.timestamp).toLocaleTimeString('en-MY', { timeZone: 'Asia/Kuala_Lumpur' });
  return `[${time}] ${role}: ${msg.content}`;
}).join('\n');

// Format products - FIXED: Use product_name, stock_quantity, images array
const productsList = products.map(p => {
  const imageUrl = p.images && p.images.length > 0 ? p.images[0] : null;
  return `- ${p.product_name} (RM ${p.price})${p.description ? `: ${p.description}` : ''}${p.stock_quantity !== undefined ? ` [Stock: ${p.stock_quantity}]` : ''}${imageUrl ? ` [Image: ${imageUrl}]` : ''}`;
}).join('\n');

// Format knowledge base - FIXED: Use file_name and extracted_content
const kbArticles = knowledgeBase.map(k =>
  `### ${k.file_name || 'Document'}\n${k.extracted_content || k.content || ''}`
).join('\n\n');

// Create contact object for session
const contact = {
  id: fromNumber.replace(/[^0-9]/g, ''),
  phone: fromNumber,
  name: fromNumber
};

// Return formatted data for AI Agent
return {
  json: {
    // AI Agent expects chatInput field
    chatInput: message,

    // Contact info for session management
    contact: contact,
    phone: fromNumber,

    // Chatbot configuration
    chatbotConfig: {
      id: chatbot.id,
      name: chatbot.name || 'Assistant',
      company: chatbot.company_name || '',
      industry: chatbot.industry || '',
      systemPrompt: chatbot.system_prompt || `You are a helpful AI assistant.`,
      businessContext: chatbot.business_context || '',
      complianceRules: chatbot.compliance_rules || [],
      responseGuidelines: chatbot.response_guidelines || []
    },

    // Context data
    products: products,
    productsList: productsList,
    knowledgeBase: knowledgeBase,
    kbArticles: kbArticles,
    conversationHistory: formattedHistory,

    // Metadata
    timestamp: new Date().toISOString(),
    messageType: 'text',
    sessionId: `whatsapp_${contact.id}_${Date.now()}`,

    // Debug
    debug: {
      hasProducts: products.length > 0,
      hasKB: knowledgeBase.length > 0,
      hasHistory: conversationHistory.length > 0,
      source: 'whatsapp_webhook'
    }
  }
};

